mapper.select:
SELECT a.* From cc a WHERE 1=1
AND shDelNy = #{shDelNy}
AND Seq = #{shValue} AND codeGroup LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO cc (
	codeNameK
	,codeNameEn
	,turn
	,codeAn


)
VALUES(
	#{codeNameK}
	,#{codeNameEn}
	,#{turn}
	,#{codeAn}

)

	SELECT last_insert_id()

mapper.update:
UPDATE cc
SET
	codeNameK = #{codeNameK}
	,codeNameEn = #{codeNameEn}
	,turn = #{turn}
	,codeAn = #{codeAn}
	,shDelNy = #{shDelNy} 
WHERE
	seq = #{seq}

