mapper.sql:
FROM cc a
WHERE 1=1
	AND delNy = #{shDelNy}

		AND seq = #{shValue}
		AND codeNameK LIKE CONCAT('%',#{shValue},'%')
		AND codeNameEn LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO cc (
	codeNameKo
	,codeNameEn
	,turn
	,codeAn
	,delNy
)
VALUES(
	#{codeNameKo}
	,#{codeNameEn}
	,#{turn}
	,#{codeAn}
	,0

)

	SELECT last_insert_id()

mapper.update:
UPDATE cc
SET
	codeNameKo = #{codeNameKo}
	,codeNameEn = #{codeNameEn}
	,turn = #{turn}
	,codeAn = #{codeAn}
	,delNy = #{delNy} 
WHERE
	seq=#{seq}
