mapper.select:
SELECT a.* From cc a WHERE 1=1
AND shDelNy = #{shDelNy}
AND Seq = #{shValue} AND codeGroup LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO cc (
	codeNameK
	,codeNameEn
	,turn
	,codeAn
	,shDelNy

)
VALUES(
	#{codeNameK}
	,#{codeNameEn}
	,#{turn}
	,#{codeAn}
	,0
)

	SELECT last_insert_id()
