mapper.sql:
FROM member a
WHERE 1=1
	AND delNy = #{shDelNy}

		AND seq = #{shValue}
		AND id LIKE CONCAT('%',#{shValue},'%')
		AND name LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO member (
	id
	,password
	,name
	,gender
	,postNumber
	,roadAddress
	,numberAddress
	,detailedAddress
	,delNy
	,useNy
	,email
	,number
	,dob
	,emailId
	,telecom
)
VALUES(
	#{id}
	,#{password}
	,#{name}
	,#{gender}
	,#{postNumber}
	,#{roadAddress}
	,#{numberAddress}
	,#{detailedAddress}
	,0
	,0
	,#{email}
	,#{number}
	,#{dob}
	,#{emailId}
	,#{telecom}
)

	SELECT last_insert_id()

mapper.update:
UPDATE member
SET
	id = #{id}
	,password = #{password}
	,name = #{name}
	,gender = #{gender}
	,dob = #{dob}
	,emailId = #{emailId}
	,email =#{email}
	,number = #{number}
	,postNumber = #{postNumber}
	,roadAddress = #{roadAddress}
	,numberAddress = #{numberAddress}
	,detailedAddress = #{detailedAddress} 
WHERE
	seq = #{seq}

mapper.select:
select
	id
from member
where 1=1
	and name = #{name}
	and dob = #{dob}

select
	count(*)
from member
where 1=1
	and id = #{id}
	and name = #{name}
	and dob = #{dob}

mapper.update:
UPDATE member
SET
	password = ${password}
where 1=1
	and id = #{id}
	and name = #{name}
	and dob = #{dob}
