mapper.sql:
FROM member a
WHERE 1=1
	AND delNy = #{shDelNy}

		AND seq = #{shValue}
		AND id LIKE CONCAT('%',#{shValue},'%')
		AND name LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO member (
	id
	,password
	,name
	,gender
	,postNumber
	,roadAddress
	,numberAddress
	,detailedAddress
	,shDelNy
	,delNy
	,email
	,number
	,dob
)
VALUES(
	#{id}
	,#{password}
	,#{name}
	,#{gender}
	,#{postNumber}
	,#{roadAddress}
	,#{numberAddress}
	,#{detailedAddress}
	,0
	,0
	,#{email}
	,#{number}
	,#{dob}
)

	SELECT last_insert_id()

mapper.update:
UPDATE member
SET
	id = #{id}
	,password = #{password}
	,name = #{name}
WHERE
	seq = #{seq}
