mapper.select:
SELECT a.* FROM member a WHERE 1=1
AND shDelNy = #{shDelNy}
AND Seq = #{shValue} AND codeGroup LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO member (
	id
	,password
	,name
	,gender
)
VALUES(
	#{id}
	,#{password}
	,#{name}
	,#{gender}
)

	SELECT last_insert_id()

mapper.update:
UPDATE member
SET
	id = #{id}
	,password = #{password}
	,name = #{name}
WHERE
	seq = #{seq}
